mapper.select:
    select * from stock_abstract_info 
    
	
		clp=#{clp,jdbcType=VARCHAR}
	
	
		and cust_id = #{custId,jdbcType=VARCHAR}
	    
	
		and so = #{so,jdbcType=VARCHAR}
	    
	
		and po = #{po,jdbcType=VARCHAR}
	    
	
		and item = #{item,jdbcType=VARCHAR}
	    
	
		and cntr_no = #{cntrNo,jdbcType=VARCHAR}
	   
	
		and shipped_date = #{shippedDate,jdbcType=VARCHAR}
	   
    	
    order by id  LIMIT #{beginSize},#{size}

    select count(1) from stock_abstract_info 
    
	
		clp=#{clp,jdbcType=VARCHAR}
	
	
		and cust_id = #{custId,jdbcType=VARCHAR}
	    
	
		and so = #{so,jdbcType=VARCHAR}
	    
	
		and po = #{po,jdbcType=VARCHAR}
	    
	
		and item = #{item,jdbcType=VARCHAR}
	    
	
		and cntr_no = #{cntrNo,jdbcType=VARCHAR}
	   
	
		and shipped_date = #{shippedDate,jdbcType=VARCHAR}
