mapper.select:
     select count(distinct t1.inbound_no) from stock_abstract_info t1 , stock_info t2 
     
     	and t1.inbound_no = t2.inbound_no and so='S10SZX1948003' 
     	
			and t1.inbound_no = #{inboundNo,jdbcType=VARCHAR}
			
		
			and t1.cust_id = #{custId,jdbcType=VARCHAR}

     select distinct t1.* from stock_abstract_info t1 , stock_info t2 
     
     	and t1.inbound_no = t2.inbound_no and so='S10SZX1948003' 
     	
			and t1.inbound_no = #{inboundNo,jdbcType=VARCHAR}
			
		
		and t1.cust_id = #{custId,jdbcType=VARCHAR}
		        
     
    order by t1.rcvd_date , t1.inbound_no  LIMIT #{beginSize},#{size}

    select * from stock_abstract_info t1 
    
	
		and t1.inbound_no = #{inboundNo,jdbcType=VARCHAR}
		
	
		and t1.cust_id = #{custId,jdbcType=VARCHAR}
			
    	
    order by t1.rcvd_date , t1.inbound_no  LIMIT #{beginSize},#{size}

    select count(1) from stock_abstract_info t1
    
	
		and t1.inbound_no = #{inboundNo,jdbcType=VARCHAR}
		
	
		and t1.cust_id = #{custId,jdbcType=VARCHAR}
